     uint8_t   buf [ 4 ];
     int   rc ;

     rc   =   fs_open ( "/data/parts/1.bin" ,  FS_ACCESS_READ ,  &file );
     if  ( rc   ==   0 ) {
         /* Advance to offset 8. */ 
         rc   =   fs_seek ( file ,  8 );
         if  ( rc   ==   0 ) {
             /* Read bytes 8, 9, 10, and 11. */ 
             rc   =   fs_read ( file ,  4 ,  buf ,  &bytes_read );
             if  ( rc   ==   0 ) {
                 /* buf now contains up to 4 bytes of file data. */ 
                 console_printf ( "read %u bytes\n" ,  bytes_read )
            }
        }

         /* Close the file. */ 
         fs_close ( file );
    }

     return   rc   ==   0   ?   0   :   - 1 ;
}

The following example creates a file and then immediately unlinks it.  By unlinking the file, this function prevents other OS tasks from accessing it.  When the function closes the file, it is deleted from the disk.  int  process_data ( void )
{
     struct   fs_file   *file ;
     int   rc ;

     /* If the file doesn't exist, create it.  If it does exist, truncate it to       * zero bytes.       */ 
     rc   =   fs_open ( "/tmp/buffer.bin" ,  FS_ACCESS_WRITE   |   FS_ACCESS_TRUNCATE ,  &file );
     if  ( rc   ==   0 ) {
         /* Unlink the file so that other tasks cannot access it. */ 
         fs_unlink ( "/tmp/buffer.bin" )

         /* <use the file as a data buffer> */ 

         /* Close the file.  This operation causes the file to be deleted from           * the disk because it was unlinked earlier (and it has no other open           * file handles).           */ 
         fs_close ( file );
    }

     return   rc   ==   0   ?   0   :   - 1 ;
}

int  write_config ( void )
{
     struct   fs_file   *file ;
     int   rc ;

     /* If the file doesn't exist, create it.  If it does exist, truncate it to       * zero bytes.       */ 
     rc   =   fs_open ( "/settings/config.txt" ,  FS_ACCESS_WRITE   |   FS_ACCESS_TRUNCATE ,
                  &file );
     if  ( rc   ==   0 ) {
         /* Write 5 bytes of data to the file. */ 
         rc   =   fs_write ( file ,  "hello" ,  5 );
         if  ( rc   ==   0 ) {
             /* The file should now contain exactly five bytes. */ 
             assert ( fs_filelen ( file )  ==   5 );
        }

         /* Close the file. */ 
         fs_close ( file );
    }

     return   rc   ==   0   ?   0   :   - 1 ;
}

This example demonstrates reading a small text file in its entirety and printing its contents to the console.  int  print_status ( void )
{
     uint32_t   bytes_read ;
     uint8_t   buf [ 16 ];
     int   rc ;

     /* Read up to 15 bytes from the start of the file. */ 
     rc   =   fsutil_read_file ( "/cfg/status.txt" ,  0 ,  sizeof   buf   -   1 ,  buf ,
                           &bytes_read );
     if  ( rc   !=   0 )  return   - 1 ;

     /* Null-terminate the string just read. */ 
     buf [ bytes_read ]  =   '\0' ;

     /* Print the file contents to the console. */ 
     console_printf ( "%s\n" ,  buf );

     return   0 ;
}

This example creates a 4-byte file.  int  write_id ( void )
{
